  async generateQRCode(qrData: string): Promise<string> {
    try {
      const qrCodeDataURL = await QRCode.toDataURL(qrData); // Generate QR code data URL
      return qrCodeDataURL;
    } catch (error) {
      console.error('Error generating QR code:', error);
      throw error;
    }
  }
